import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LocationForm from './LocationForm';
import WeatherDisplay from './WeatherDisplay';
import WeatherForecast from './WeatherForecast';
import Nav from './Nav';
import 'bootstrap/dist/css/bootstrap.min.css';
import {BrowserRouter as Router, Route, Routes, Switch} from "react-router-dom";
import PopularDisplay from "./PopularDisplay";

const WeatherApp = () => {
    const [weatherData, setWeatherData] = useState(null);
    const [forecastData, setForecastData] = useState(null);
    const [localTime, setLocalTime] = useState('');
    const [popularWeatherData, setPopularWeatherData] = useState([]);

    const getCoordinatesByZipCode = async (zipCode) => {
        try {
            const response = await axios.get(
                `https://maps.googleapis.com/maps/api/geocode/json?address=${zipCode}&key=AIzaSyBiFa6AYtxVXmpHKNLLCyy1w97Li0mlCDM`
            );
            const { lat, lng } = response.data.results[0].geometry.location;
            getWeatherByCoordinates(lat, lng);
            getForecastByCoordinates(lat, lng);
        } catch (error) {
            console.error('Error getting coordinates:', error);
        }
    };

    const getWeatherByCoordinates = async (lat, lng) => {
        try {
            const response = await axios.get(
                `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lng}&units=imperial&appid=ac8cfff2ad4aa781c5ae31f5bc7a2d07`
            );
            const weather = {
                temperature: response.data.main.temp,
                description: response.data.weather[0].description,
                humidity: response.data.main.humidity,
                windSpeed: response.data.wind.speed,
            };
            setWeatherData(weather);
        } catch (error) {
            console.error('Error getting weather:', error);
        }
    };

    const getForecastByCoordinates = async (lat, lng) => {
        try {
            const response = await axios.get(
                `http://api.weatherapi.com/v1/forecast.json?key=2a1769a877554f7e88133029231105&q=48.8567,2.3508&days=3`
            );
            const forecast = response.data.forecast.forecastday.map((item) => ({
                dateTime: item.date,
                temperature: item.day.avgtemp_f,
                description: item.day.condition.text,
                humidity: item.day.avghumidity,
                windSpeed: item.day.maxwind_mph,
            }));
            setForecastData(forecast);
        } catch (error) {
            console.error('Error getting forecast:', error);
        }
    };

    const fetchPopularWeatherData = async () => {
        const weatherDataArray = [];
        for (const location of popularLocations) {
            const { zipCode } = location;
            try {
                const { lat, lng } = await getCoordinatesByZipCode(zipCode);
                const weatherData = await getWeatherByCoordinates(lat, lng);
                weatherDataArray.push(weatherData);
            } catch (error) {
                console.error('Error fetching popular weather data:', error);
            }
        }
        setPopularWeatherData(weatherDataArray);
    };

    useEffect(() => {
        const updateLocalTime = () => {
            const currentTime = new Date();
            const options = {
                hour: 'numeric',
                minute: 'numeric',
                second: 'numeric',
                timeZoneName: 'short',
            };
            const localTimeString = currentTime.toLocaleString('en-US', options);
            setLocalTime(localTimeString);
        };

        const timer = setInterval(updateLocalTime, 1000);
        return () => {
            clearInterval(timer);
        };
    }, []);

    return (
        <Router>
            <div>
                <h1>React Weather App</h1>
                <p>Local Time: {localTime}</p>
                <Nav />
                <Routes>
                    <Route
                        path="/"
                        element={
                            <div>
                                <LocationForm onFormSubmit={getCoordinatesByZipCode} />
                                {weatherData && <WeatherDisplay weather={weatherData} />}
                                {forecastData && <WeatherForecast forecast={forecastData} />}
                            </div>
                        }
                    />
                    <Route path="/popular" element={<PopularDisplay />} />
                </Routes>
            </div>
        </Router>
        // <div>
        //     <h1>React Weather App</h1>
        //     <p>Local Time: {localTime}</p>
        //     <LocationForm onFormSubmit={getCoordinatesByZipCode} />
        //     {weatherData && <WeatherDisplay weather={weatherData} />}
        //     {forecastData && <WeatherForecast forecast={forecastData} />}
        // </div>
    );
};

export default WeatherApp;
