import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './App.css';


function App() {
  // settings
  const [numGuessesAllowed, setNumGuessesAllowed] = useState(3);
  const [minNumRange, setMinNumRange] = useState(0);
  const [maxNumRange, setMaxNumRange] = useState(10);

  // stats
  const [correctGuesses, setCorrectGuesses] = useState(0);
  const [gameGuessHistory, setGameGuessHistory] = useState([]);
  const [avgNumOfGuesses, setAvgNumGuesses] = useState(0);

  // current game
  const [numGuesses, setNumGuesses] = useState(0);
  const [guess, setGuess] = useState('');
  const [message, setMessage] = useState('');
  const [answer, setAnswer] = useState(Math.floor(Math.random() * (maxNumRange - minNumRange + 1)) + minNumRange);
  const [allGuessesVal, setAllGuessesVal] = useState([]);
  const [gameOver, setGameOver] = useState(false);


  const incrementNumGuesses = () => {
    setNumGuesses(numGuesses + 1);
  }

  const incrementCorrectGuesses = () => {
    setCorrectGuesses(correctGuesses + 1);
  }

  const calculateAverage = () => {
    let avg = gameGuessHistory.length > 0 ? gameGuessHistory.reduce((total, num) => total + num)/gameGuessHistory.length : 0;
    setAvgNumGuesses((avg).toFixed(2));
  }

  const newGame = () => {
    setGuess('');
    setMessage('');
    setNumGuesses(0);
    setAllGuessesVal([]);
    setAnswer(Math.floor(Math.random() * (maxNumRange - minNumRange + 1)) + minNumRange);
    setGameOver(false);
  }

  return (
      <div>
        <Router>
          <Routes>
            <Route path="/" element={
              <Game
                  // Settings Component
                  numGuessesAllowed={numGuessesAllowed}
                  minNumRange={minNumRange}
                  setMinNumRange={setMinNumRange}
                  maxNumRange={maxNumRange}
                  setMaxNumRange={setMaxNumRange}
                  calculateAverage={calculateAverage}
                  numGuesses={numGuesses}
                  setNumGuesses={setNumGuesses}
                  guess={guess}
                  setGuess={setGuess}
                  message={message}
                  setMessage={setMessage}
                  answer={answer}
                  setAnswer={setAnswer}
                  allGuessesVal={allGuessesVal}
                  setAllGuessesVal={setAllGuessesVal}
                  incrementNumGuesses={incrementNumGuesses}
                  correctGuesses={correctGuesses}
                  incrementCorrectGuesses={incrementCorrectGuesses}
                  gameGuessHistory={gameGuessHistory}
                  setGameGuessHistory={setGameGuessHistory}
                  newGame={newGame}
                  gameOver={gameOver}
                  setGameOver={setGameOver}
              />}/>
            <Route path="/settings" element={
              <Settings
                  numGuessesAllowed={numGuessesAllowed}
                  setNumGuessesAllowed={setNumGuessesAllowed}
                  minNumRange={minNumRange}
                  setMinNumRange={setMinNumRange}
                  maxNumRange={maxNumRange}
                  setMaxNumRange={setMaxNumRange}
                  avgNumOfGuesses={avgNumOfGuesses}
                  newGame={newGame}
              />}/>
            <Route path="/stats" element={
              <Stats
                  calculateAverage={calculateAverage}
                  correctGuesses={correctGuesses}
                  avgNumOfGuesses={avgNumOfGuesses}
                  gameGuessHistory={gameGuessHistory}
              />} />
          </Routes>
        </Router>
      </div>
  );
}


function Game(props) {

  function handleGuess(event) {
    event.preventDefault();

    props.incrementNumGuesses();
    props.setAllGuessesVal([...props.allGuessesVal, props.guess]);

    // reached num of allowed guesses
    console.log(props.minNumRange, props.maxNumRange)
    if (props.numGuesses >= props.numGuessesAllowed - 1) {
      props.setMessage(`The number of allowed guesses, ${props.numGuessesAllowed} guesses, has been reached. The correct answer is ${props.answer}. Try again!`);
      props.setGameGuessHistory([...props.gameGuessHistory, props.numGuesses + 1]);
      console.log(props.gameGuessHistory)
      props.setGameOver(true);
    }
    // out of range, min
    else if (props.guess < props.minNumRange) {
      props.setMessage(`Out of range: min ${props.minNumRange}, guess ${props.guess}`);
    }
    // out of range, max
    else if (props.guess > props.maxNumRange) {
      props.setMessage(`Out of range: max ${props.maxNumRange}, guess ${props.guess}`);
    }
    // num of allowed guesses still not reached
    else {
      // guess is correct
      if (props.guess === props.answer) {
        //
        props.setMessage(`You guessed the right number ${props.answer}!`);
        props.incrementCorrectGuesses();
        props.setGameGuessHistory([...props.gameGuessHistory, props.numGuesses]);
        props.setGameOver(true);
      }
      // guess is less than
      else if (props.guess < props.answer) {
        props.setMessage(`${props.guess} is too low! Guess again!`);
      }
      // guess is greater than
      else if (props.guess > props.answer) {
        props.setMessage(`${props.guess} is too high! Guess again!`);
      }
      props.setGuess('');
    }
  }

  function handleChange(event) {
    props.setGuess(Number(event.target.value));
  }

  return (
      <div className='Game-center'>
        <Header />
        <h3>Guess the Number</h3>
        <form onSubmit={handleGuess}>
          <input disabled={props.gameOver} type="number" value={props.guess} onChange={handleChange} />
          <button disabled={props.gameOver} onClick={handleGuess} type="submit">Submit Guess</button>
        </form>
        <p>{props.message}</p>
        <p>Total number of guesses: {props.numGuesses}</p>
        <p>
          Your guesses: [{props.allGuessesVal.map((item, index) => {
          return <span key={item}> {item}, </span>
        })}]

        </p>
        {!props.gameOver ? null : <button onClick={props.newGame} type="submit">New Game</button>}
        <p>Random Number: {props.answer}</p>
      </div>
  );
}


function Nav() {
  return (
      <ul id='nav'>
        <li><Link to="/">Game</Link></li>
        <li><Link to="/settings">Settings</Link></li>
        <li><Link to="/stats">Player Stats</Link></li>
      </ul>
  );
}

function Header() {
  return (
      <div className='App'>
        <h2>Guessing Game</h2>
        <Nav />
      </div>
  );
}


function Settings(props) {
  function handleNumGuessesChange(event) {
    props.setNumGuessesAllowed(Number(event.target.value));
  }

  function handleMinNumRangeChange(event) {
    props.setMinNumRange(Number(event.target.value));
    props.newGame();
  }

  function handleMaxNumRangeChange(event) {
    props.setMaxNumRange(Number(event.target.value));
    props.newGame();
  }

  return (
      <div className="Game-center">
        <Header />
        <h3>User Settings</h3>
        <form>
          <label>
            Number of guesses allowed:
            <input
                type="number"
                value={props.numGuessesAllowed}
                onChange={handleNumGuessesChange}
            />

          </label>
          <br />
          <label>
            Number range to guess:
            Min:
            <input
                type="number"
                value={props.minNumRange}
                onChange={handleMinNumRangeChange}
            />
            Max:
            <input
                type="number"
                value={props.maxNumRange}
                onChange={handleMaxNumRangeChange}
            />
          </label>
        </form>
      </div>
  );

}


function Stats(props) {
  props.calculateAverage();

  return (
      <div className="Game-center">
        <Header />
        <h3>Player Stats</h3>
        <p>Number of correct guesses: {props.correctGuesses}</p>
        <p>Average number of guesses: {props.avgNumOfGuesses}</p>
        <p>Total Number of guesses: [{props.gameGuessHistory.length > 0 ? props.gameGuessHistory.map((item, index) => {
          return <span key={item}> {item}, </span>
        }) : []}]</p>
      </div>
  );
}

export default App;


