import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link} from 'react-router-dom';
import './App.css';


function App() {

    // const weatherData = [
    //     { id: 1, city: 'London', temperature: 20 },
    //     { id: 2, city: 'Paris', temperature: 25 },
    //     { id: 3, city: 'New York', temperature: 30 },
    // ];

  return (
    <div>
        <Router>
            <Routes>
                <Route path="/" element={
                    <Weather

                    />}/>
                <Route path="/favorites" element={
                    <Favorites
                    />}/>
                <Route path="/popular" element={
                    <Popular
                    />}/>
            </Routes>
        </Router>
    </div>
  );
}

function Nav() {
    return (
        <ul id='nav'>
            <li><Link to="/"/>Weather<Link/></li>
            <li><Link to="/favorites"/>Favorites<Link/></li>
            <li><Link to="/popular"/>Popular<Link/></li>
        </ul>

    );
}

function Header() {
    return (
        <div className='App'>
            <h2>Weather App</h2>
            <Nav />
        </div>
    );
}

function Weather(props) {
    const [city, setCity] = useState('');
    const [temperature, setTemperature] = useState('');

    const handleFormSubmit = (e) => {
        e.preventDefault();
        const cityWeather = weatherData.find((data) => data.city === city);
        if (cityWeather) {
            setTemperature(cityWeather.temperature);
        } else {
            setTemperature('City not found');
        }
        setCity('');
    };

    return (
        <div className="app-center">
            <Header />
            <form onSubmit={handleFormSubmit}>
                <input
                    type="text"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    placeholder="Enter city"
                />
                <button type="submit">Get Weather</button>
            </form>
            <p>{temperature}</p>
        </div>
    )
}

function Favorites() {
    return (
        <div className="app-center">
            <Header />
            <h3>Favorites</h3>


        </div>
    )
}

function Popular() {
    return (
        <div className="app-center">
            <Header />
            <h3>Popular Locations</h3>


        </div>
    )
}


export default App;
